mapper.select:
select *
from plans
where user_id = #{user_id}
order by start_date;

mapper.delete:
delete from plans
where plan_id = #{plan_id}
and user_id = #{user_id}

mapper.update:
    	update plan_places
		set visit_date = #{visit_date},
			visit_time = #{visit_time},
			place_name = #{place_name},
			description = #{description}
		where plan_place_id = #{plan_place_id}
